// src/AudioDemo.js

import React from 'react';
import './AudioDemo.css'; // Importing the CSS

function AudioDemo() {
    const playAudio = () => {
        // This is where you'd integrate the audio playing functionality
        alert("Playing Lucy's audio demo!"); // Placeholder
    };

    return (
        <div className="audio-demo-container">
            <img src="/path_to_qsic_logo.png" alt="Qsic Logo" className="qsic-logo" />
            <div className="play-button" onClick={playAudio}>
                ▶️
            </div>
            <div className="cta">
                Interested in Lucy? <a href="/contact_link" className="cta-link">Contact us for a demo</a>
            </div>
        </div>
    );
}

export default AudioDemo;
